CREATE TABLE IF NOT EXISTS {DBPREF}config ( name varchar(20) character set latin1 collate latin1_spanish_ci NOT NULL default '',  value text character set latin1 collate latin1_spanish_ci NOT NULL,  id int(11) NOT NULL auto_increment,  PRIMARY KEY  (id)) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=3 ;
CREATE TABLE IF NOT EXISTS {DBPREF}module ( id varchar(120) NOT NULL,   name varchar(120) NOT NULL,   content text NOT NULL,   PRIMARY KEY  (id) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS {DBPREF}user  ( id  int(12) NOT NULL auto_increment,    name  varchar(255) collate latin1_spanish_ci NOT NULL,    surname  varchar(255) collate latin1_spanish_ci NOT NULL,    email  varchar(255) collate latin1_spanish_ci NOT NULL,    user  varchar(30) collate latin1_spanish_ci default NULL,    pass  varchar(255) collate latin1_spanish_ci NOT NULL,    level  int(12) NOT NULL default '1',    comments  text collate latin1_spanish_ci NOT NULL,    date_registration  timestamp NULL default CURRENT_TIMESTAMP,    active  varchar(1) collate latin1_spanish_ci default 'N',    last_pass_change  date NOT NULL,    login_attempts  int(1) unsigned default '0',    last_ip_connection  varchar(18) collate latin1_spanish_ci default '0.0.0.0',   PRIMARY KEY  ( id ) ) ENGINE=MyISAM  DEFAULT CHARSET=latin1 COLLATE=latin1_spanish_ci;
CREATE TABLE IF NOT EXISTS {DBPREF}new ( id int(12) NOT NULL AUTO_INCREMENT, titulo varchar(255) COLLATE latin1_spanish_ci NOT NULL DEFAULT '', subtitulo text COLLATE latin1_spanish_ci NOT NULL, descripcion text COLLATE latin1_spanish_ci NOT NULL, fecha timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, adjunto varchar(255) COLLATE latin1_spanish_ci NOT NULL DEFAULT '', activo varchar(3) COLLATE latin1_spanish_ci NOT NULL DEFAULT '', PRIMARY KEY (id) ) ENGINE=MyISAM DEFAULT CHARSET=latin1 COLLATE=latin1_spanish_ci AUTO_INCREMENT=1 ;

INSERT INTO {DBPREF}module (id, name, content) VALUES('list', '', '<? \r\n	$web = new simple( true , 100, ''iud'', array(table =>''{NOMG}'') );\r\n	$web->Title = "{NOMG}";\r\n	${NOMG}s = $web->Object;\r\n?>\r\n\r\n	<h1>{NOMG}</h1>\r\n\r\n	<div class="tableList">\r\n		<table cellpadding="0" cellspacing="0">\r\n			\r\n			<thead>\r\n				<tr>\r\n					<th><?=_NAME_?></th>\r\n					<th><?=_EDIT_?></th>\r\n					<th class="last"><a href="javascript: showModalWindow(''<?=$web->ModalWDest ?>0/'')" class="item_add"><?=_ADD_?></a></th>\r\n				</tr>\r\n				\r\n			</thead>\r\n			\r\n			<tbody>\r\n				\r\n			<? while (${NOMG} = $db->fetchNextObject(${NOMG}s)) { ?>\r\n				<tr>\r\n					<td>\r\n						<?=${NOMG}->id?>\r\n					</td>\r\n					<td>\r\n						<a href="javascript: showModalWindow(''<?=$web->ModalWDest . ${NOMG}->id ?>/'')" class="item_edit"><?=_EDIT_?></a>\r\n					</td>\r\n					<td>\r\n						<a href="javascript: showModalWindowDelete(''<?=$web->ModalWDest . ${NOMG}->id ?>/delete/'')" class="item_delete"><?=_DELETE_?></a>\r\n					</td>\r\n				</tr>\r\n			<? } ?>	\r\n								\r\n			</tbody>\r\n			\r\n			<tfoot>\r\n				<tr>\r\n					<td colspan="3" id="pagination">\r\n						<?=$web->Pagination?>\r\n					</td>\r\n				</tr>\r\n			</tfoot>\r\n		  	\r\n		</table>\r\n	</div>\r\n	\r\n	<form id="frmPagination" method="post">\r\n\r\n		<input type="hidden" id="order_item" name="order_item" value="id" />\r\n		<input type="hidden" id="order_dir_item" name="order_dir_item" value="asc" />\r\n		\r\n		<!--\r\n		<input type="hidden" id="filters" name="filters" value="name,surname" />\r\n		<input type="hidden" id="filter_name" name="filter_name" value="" />\r\n		<input type="hidden" id="filter_surname" name="filter_surname" value="" />\r\n		-->\r\n		\r\n		<?=$web->PaginationInputs ?>\r\n		\r\n	</form>\r\n\r\n	<div id="modalWindow" ></div>\r\n	\r\n<? $web->loadPage(($_GET[''val1'']!=''ajax'')?''admin'':''ajax''); ?>'),
INSERT INTO {DBPREF}module (id, name, content) VALUES('page', '', '<? \r\n  $web = new simple( true , 100, ''iud'');\r\n    ${NOMG} = $web->Object;\r\n?>\r\n    \r\n    <form name="frm{NOMG}" id="frm{NOMG}" method="post">\r\n              \r\n        <h1>{NOMG}</h1>\r\n        <a href="#" id="close"><?=_CLOSE_?></a>\r\n        \r\n        <table class="tableData">\r\n            \r\n            {CAMPOSG}\r\n            \r\n            <!-- BUTTONS -->\r\n            <tr>\r\n                <td colspan="2" class="buttonsData">\r\n                    <input type="button" id="save" name="save" value="<?=_SAVE_?>" />\r\n                  <? if(${NOMG}->id != '''') { ?>\r\n                    <input type="button" id="delete" name="delete" value="<?=_DELETE_?>" />\r\n                  <? } ?>\r\n                </td>\r\n            </tr>\r\n        </table>\r\n    \r\n        <input type="hidden" id="id" name="id" value="<?=${NOMG}->id?>" />\r\n        <input type="hidden" id="table_item" name="table_item" value="<?=db_pref?>{NOMG}" />\r\n        <input type="hidden" id="params_item" name="params_item" value="{CAMPOSGLIST}" />\r\n        <input type="hidden" id="name_item" name="name_item" value="{NOMG}" />\r\n        <input type="hidden" id="id_item" name="id_item" value="id" />\r\n    </form>\r\n\r\n	<script language="JavaScript">\r\n	\r\n	    	// S A V E\r\n		$(''#save'').click(function () { \r\n				// Ajax Form options & initialize\r\n				$(''#frm{NOMG}'').ajaxForm({ \r\n				    target:     ''#modalWindow'', \r\n				    url:        ''<?=apppath?>admin/{NOMG}/<?=${NOMG}->id?>/save/'', \r\n				    success:    function() { \r\n					$(''.simple_message'').css(''top'',$(window).scrollTop()).fadeIn(200).delay(800).slideUp(1600);\r\n				    } \r\n				});   \r\n				     			\r\n		    $(''#frm{NOMG}'').submit(); \r\n		});\r\n		    \r\n		// D E L E T E\r\n		$(''#delete'').click(function () { \r\n\r\n				// Ajax Form options & initialize\r\n				$(''#frm{NOMG}'').ajaxForm({ \r\n				    target:     ''#modalWindow'', \r\n				    url:        ''<?=apppath?>admin/{NOMG}/<?=${NOMG}->id?>/delete/'', \r\n				    success:    function() { \r\n					$(''.simple_message'').css(''top'',$(window).scrollTop()).fadeIn(200).delay(800).slideUp(1600);\r\n				    } \r\n				});   \r\n				     			\r\n		    $(''#frm{NOMG}'').submit(); \r\n\r\n		});\r\n			\r\n		$(''#close'').click(function () { $(''#modalWindow'').fadeOut(1000); pagination_loadTableList(); });\r\n\r\n	</script>\r\n  \r\n<? $web->loadPage(''ajax''); ?>');

INSERT INTO {DBPREF}config (name, value, id) VALUES ('login_expires', '9999', NULL),('login_attempts', '10', NULL), ('web_name', '{WEBNAME}', NULL);
